from streamlit.testing.v1 import AppTest


def test_text_area():
    at = AppTest.from_file("pages/1_üìù_Summaraizer.py")

    at.run(timeout=30)

    text = """–î–∞–Ω–Ω–æ–µ –∏—Å—Å–ª–µ–¥–æ–≤–∞–Ω–∏–µ –æ–±—É—Å–ª–æ–≤–ª–µ–Ω–æ —à–∏—Ä–æ–∫–∏–º –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º –≤—Å–µ–Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–Ω–æ–π –∫–∞–º–µ—Ä—ã –∏ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ
    —Å–≤–µ—Ç–∞ –≤ –∫–∞—á–µ—Å—Ç–≤–µ –∏—Å—Ç–æ—á–Ω–∏–∫–æ–≤ –ø–æ–ª—É—á–µ–Ω–∏—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –∏–∑ –æ–∫—Ä—É–∂–∞—é—â–µ–π —Å—Ä–µ–¥—ã. –ù–∞–ø—Ä–∏–º–µ—Ä, –∫–∞—Ä—Ç–æ–≥—Ä–∞—Ñ–∏—Ä–æ–≤–∞–Ω–∏–µ –∏–ª–∏
    —Ä–µ–∫–æ–Ω—Å—Ç—Ä—É–∫—Ü–∏—è –ø–æ–º–µ—â–µ–Ω–∏—è –∏ –Ω–∞–≤–∏–≥–∞—Ü–∏—è –º–æ–±–∏–ª—å–Ω—ã—Ö —Ä–æ–±–æ—Ç–æ–≤ ‚Äì —ç—Ç–æ –ø–æ—Ç–µ–Ω—Ü–∏–∞–ª—å–Ω—ã–µ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è. –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
    –≤—Å–µ–Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–Ω–æ–π –∫–∞–º–µ—Ä—ã –¥–∞–µ—Ç —à–∏—Ä–æ–∫–∏–π —É–≥–æ–ª –æ–±–∑–æ—Ä–∞, —á—Ç–æ —è–≤–ª—è–µ—Ç—Å—è –ø—Ä–µ–∏–º—É—â–µ—Å—Ç–≤–æ–º –≤ —Å—Ä–∞–≤–Ω–µ–Ω–∏–∏ —Å–æ —Å—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–º–∏
    –∫–∞–º–µ—Ä–∞–º–∏. –í —Å–∏–ª—É –≤—ã—Å–æ–∫–æ–≥–æ –∏—Å–∫–∞–∂–µ–Ω–∏—è –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π –º–æ–∂–µ—Ç –≤—ã–∑–≤–∞—Ç—å –∑–∞—Ç—Ä—É–¥–Ω–µ–Ω–∏—è –∏–∑–≤–ª–µ—á–µ–Ω–∏–µ –æ–¥–∏–Ω–∞–∫–æ–≤—ã—Ö –ø–∏–∫—Å–µ–ª–µ–π –º–µ–∂–¥—É
    –Ω–∏–º–∏ –≤ —Å–ª—É—á–∞–µ —Å–æ —Å—Ç–µ—Ä–µ–æ–∑—Ä–µ–Ω–∏–µ–º. –ë–æ–ª–µ–µ —Ç–æ–≥–æ, —É—Å–ª–æ–≤–∏—è –æ–∫—Ä—É–∂–∞—é—â–µ–π —Å—Ä–µ–¥—ã —Å–ø–æ—Å–æ–±–Ω—ã –≤—ã–≤–æ–¥–∏—Ç—å –∏–∑ —Å—Ç—Ä–æ—è –∞–ª–≥–æ—Ä–∏—Ç–º—ã
    —Å—Ç–µ—Ä–µ–æ–∑—Ä–µ–Ω–∏—è, –Ω–∞–ø—Ä–∏–º–µ—Ä, —É—Å–ª–æ–≤–∏—è –æ—Å–≤–µ—â–µ–Ω–Ω–æ—Å—Ç–∏. –†–µ—à–µ–Ω–∏–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –Ω–∞–π–¥–µ–Ω–æ –ø—É—Ç–µ–º –∏–Ω—Ç–µ–≥—Ä–∞—Ü–∏–∏ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ
    —Å–≤–µ—Ç–∞ –≤ —Å–∏—Å—Ç–µ–º—É. –û—Å–Ω–æ–≤–Ω—ã–º –ø—Ä–µ–∏–º—É—â–µ—Å—Ç–≤–æ–º –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è —Å—Ç—Ä—É–∫—Ç—É—Ä–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ —Å–≤–µ—Ç–∞ –¥–ª—è –∞–Ω–∞–ª–∏–∑–∞ –¥–∞–Ω–Ω—ã—Ö —è–≤–ª—è–µ—Ç—Å—è –µ–≥–æ
    –ø—Ä–æ—Å—Ç–æ–π –º–µ—Ö–∞–Ω–∏–∑–º –æ–±–Ω–∞—Ä—É–∂–µ–Ω–∏—è –∏ –∏–∑–≤–ª–µ—á–µ–Ω–∏—è –∏–∑ –∏—Å—Ö–æ–¥–Ω–æ–≥–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è. –¢–∞–∫–∏–º –æ–±—Ä–∞–∑–æ–º, —Å–∏—Å—Ç–µ–º–∞ –∫–æ–º–ø—å—é—Ç–µ—Ä–Ω–æ–≥–æ –∑—Ä–µ–Ω–∏—è,
    —Å–æ—Å—Ç–æ—è—â–∞—è –∏–∑ –≤—Å–µ–Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–Ω–æ–π –∫–∞–º–µ—Ä—ã –≤ —Å–æ—á–µ—Ç–∞–Ω–∏–∏ —Å–æ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏—Ä–æ–≤–∞–Ω–Ω—ã–º —Å–≤–µ—Ç–æ–º, –≤—ã–∑—ã–≤–∞–µ—Ç –±–æ–ª—å—à–æ–π –∏–Ω—Ç–µ—Ä–µ—Å —Å—Ä–µ–¥–∏
    —É—á–µ–Ω—ã—Ö –±–ª–∞–≥–æ–¥–∞—Ä—è —à–∏—Ä–æ–∫–æ–º—É —É–≥–ª—É –æ–±–∑–æ—Ä–∞ –∏ –≤—ã—Å–æ–∫–æ–π —ç—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏ –∏–∑–º–µ—Ä–µ–Ω–∏–π. –î–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –¥–æ—Å—Ç–æ–≤–µ—Ä–Ω—ã—Ö –∏–∑–º–µ—Ä–µ–Ω–∏–π
    —Å–∏—Å—Ç–µ–º–∞ –∫–æ–º–ø—å—é—Ç–µ—Ä–Ω–æ–≥–æ –∑—Ä–µ–Ω–∏—è –Ω—É–∂–¥–∞–µ—Ç—Å—è –≤ –∫–∞–ª–∏–±—Ä–æ–≤–∫–µ. –≠—Ç–æ –æ–±—É—Å–ª–æ–≤–ª–µ–Ω–æ —Ç–µ–º, —á—Ç–æ –±–µ–∑ –∏–∑–≤–µ—Å—Ç–Ω–æ–π —Å–≤—è–∑–∏ –º–µ–∂–¥—É –∫–∞–º–µ—Ä–æ–π –∏
    –ª–∞–∑–µ—Ä–Ω–æ–π –ø–ª–æ—Å–∫–æ—Å—Ç—å—é –Ω–µ–≤–æ–∑–º–æ–∂–Ω–æ –ø—Ä–æ–≤–µ—Å—Ç–∏ –∏–∑–º–µ—Ä–µ–Ω–∏—è —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–∏–º –æ–±—Ä–∞–∑–æ–º. –î–∞–Ω–Ω–æ–µ —É—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ –º–æ–∂–Ω–æ –æ–±–æ—Å–Ω–æ–≤–∞—Ç—å,
    –ø—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä–æ–≤–∞–≤ –Ω–µ—Å–∫–æ–ª—å–∫–æ —Å—É—â–µ—Å—Ç–≤—É—é—â–∏—Ö –∏—Å—Å–ª–µ–¥–æ–≤–∞–Ω–∏–π."""

    at.text_area[0].set_value(text).run()

    at.button[0].click().run(timeout=240)
    assert (
        at.markdown[1].value
        == """–≠—Ç–æ –æ–±—É—Å–ª–æ–≤–ª–µ–Ω–æ —à–∏—Ä–æ–∫–∏–º –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º –≤—Å–µ–Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–Ω–æ–π –∫–∞–º–µ—Ä—ã –∏ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ —Å–≤–µ—Ç–∞ –≤ –∫–∞—á–µ—Å—Ç–≤–µ –∏—Å—Ç–æ—á–Ω–∏–∫–æ–≤ –ø–æ–ª—É—á–µ–Ω–∏—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –∏–∑ –æ–∫—Ä—É–∂–∞—é—â–µ–π —Å—Ä–µ–¥—ã. –î–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –¥–æ—Å—Ç–æ–≤–µ—Ä–Ω—ã—Ö –∏–∑–º–µ—Ä–µ–Ω–∏–π —Å–∏—Å—Ç–µ–º–∞ –∫–æ–º–ø—å—é—Ç–µ—Ä–Ω–æ–≥–æ –∑—Ä–µ–Ω–∏—è –Ω—É–∂–¥–∞–µ—Ç—Å—è –≤ –∫–∞–ª–∏–±—Ä–æ–≤–∫–µ. –≠—Ç–æ —è–≤–ª—è–µ—Ç—Å—è –ø—Ä–µ–∏–º—É—â–µ—Å—Ç–≤–æ–º –≤ —Å—Ä–∞–≤–Ω–µ–Ω–∏–∏ —Å–æ —Å—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–º–∏ –∫–∞–º–µ—Ä–∞–º–∏, –∫–æ—Ç–æ—Ä—ã–µ –º–æ–≥—É—Ç –≤—ã–∑–≤–∞—Ç—å –∑–∞—Ç—Ä—É–¥–Ω–µ–Ω–∏—è –∏–∑–≤–ª–µ—á–µ–Ω–∏–µ –æ–¥–∏–Ω–∞–∫–æ–≤—ã—Ö –ø–∏–∫—Å–µ–ª–µ–π –º–µ–∂–¥—É –Ω–∏–º–∏ –≤ —Å–ª—É—á–∞–µ —Å–æ —Å—Ç–µ—Ä–µ–æ–∑—Ä–µ–Ω–∏–µ–º. –†–µ—à–µ–Ω–∏–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –Ω–∞–π–¥–µ–Ω–æ –ø—É—Ç–µ–º –∏–Ω—Ç–µ–≥—Ä–∞—Ü–∏–∏ —Å—Ç—Ä—É–∫—Ç—É—Ä–∏—Ä–æ–≤–∞–Ω–Ω–æ–π —Å–≤–µ—Ç–∞ –≤ —Å–∏—Å—Ç–µ–º—É. –≠—Ç–∞ —Å–∏—Å—Ç–µ–º–∞ –º–æ–∂–µ—Ç –≤—ã–∑—ã–≤–∞—Ç—å –±–æ–ª—å—à–æ–π –∏–Ω—Ç–µ—Ä–µ—Å —Å—Ä–µ–¥–∏ —É—á–µ–Ω—ã—Ö –±–ª–∞–≥–æ–¥–∞—Ä—è —à–∏—Ä–æ–∫–æ–º—É —É–≥–ª—É –æ–±–∑–æ—Ä–∞ –∏ –≤—ã—Å–æ–∫–æ–π —ç—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏ –∏–∑–º–µ—Ä–µ–Ω–∏–π. –≠—Ç–æ—Ç –º–µ—Ç–æ–¥ –º–æ–∂–Ω–æ –æ–±–æ—Å–Ω–æ–≤–∞—Ç—å, –ø—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä–æ–≤–∞–≤ –Ω–µ—Å–∫–æ–ª—å–∫–æ —Å—É—â–µ—Å—Ç–≤—É—é—â–∏—Ö –∏—Å—Å–ª–µ–¥–æ–≤–∞–Ω–∏–π, –Ω–∞–ø—Ä–∏–º–µ—Ä, –∫–∞—Ä—Ç–æ–≥—Ä–∞—Ñ–∏—Ä–æ–≤–∞–Ω–∏–µ –∏–ª–∏ —Ä–µ–∫–æ–Ω—Å—Ç—Ä—É–∫—Ü–∏—è –ø–æ–º–µ—â–µ–Ω–∏—è –∏ –Ω–∞–≤–∏–≥–∞—Ü–∏—è –º–æ–±–∏–ª—å–Ω—ã—Ö —Ä–æ–±–æ—Ç–æ–≤ ‚Äì —ç—Ç–æ –ø–æ—Ç–µ–Ω—Ü–∏–∞–ª—å–Ω—ã–µ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è. –í —Å–∏–ª—É –≤—ã—Å–æ–∫–æ–≥–æ –∏—Å–∫–∞–∂–µ–Ω–∏—è –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π —Å–ø–æ—Å–æ–±–Ω—ã –≤—ã–≤–æ–¥–∏—Ç—å –∏–∑ —Å—Ç—Ä–æ—è –∞–ª–≥–æ—Ä–∏—Ç–º—ã —Å—Ç–µ—Ä–µ–æ–∑—Ä–µ–Ω–∏—è"""
    )


